    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        Pelicula p=VideoClubFichero.misPeliculas.favorita();
        if (p==null){
            JOptionPane.showMessageDialog(this, "no hay peliculas");
        }else{
            JOptionPane.showMessageDialog(this, "la pelicula favorita es \n"+p.getTitulo()+" con una puntuacion de: "+p.getValoracion());
        }
    }//GEN-LAST:event_jButton3ActionPerformed

