    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
        int totalPeliculas=VideoClubFichero.misPeliculas.cantidad();
        int totalPelisVistas=totalPeliculas-VideoClubFichero.misPeliculas.numeroPeliculasPorVer();
        int totalMin=VideoClubFichero.misPeliculas.totalMinutos();
        JOptionPane.showMessageDialog(this, "\nEl numero total de peliculas es:    "+totalPeliculas+"\n"
                +"\nEl numero de peliculas vistas es:   "+totalPelisVistas+"\n"
                +"\nEl numero total de minutos es de:   "+totalMin,"TOTALES",JOptionPane.INFORMATION_MESSAGE);
    }//GEN-LAST:event_jButton5ActionPerformed

